            var API = 'https://api.cryptonator.com/api/ticker';

            function getCurse(API) {
                var loadIMG = $('#loadIMG');
                $(loadIMG).show();

                $.getJSON(API, function(data) {
                    console.log(data);

                    var cursResult = data.ticker.price; // результат обмена
                    var arrayRow = '';

                    $('#content_table > tbody > tr').each(function() {
                        /* перебираем все ячейки с результатом обмена */
                        var cell = $(this).find('td:nth-child(4) > span ');
                        var coeff = $(cell).attr('data-curs'); // на сколько умножить курс
                        var result = cursResult * coeff;


                        $(cell).text(parseFloat(result.toFixed(8)));
                        arrayRow += $(this).attr('data-index', $(cell).text());
                        console.log(cursResult * coeff);

                    });

                });
                $('#content_table > tbody > tr').sort(function(a, b) { // сортируем
                        return +$(b).attr('data-index') - +$(a).attr('data-index');
                    })
                    .appendTo('#content_table > tbody'); // возвращаем в контейнер

                setTimeout(function() {
                    loadIMG.hide()
                }, 200);
            }

            getCurse(API);

            setInterval(function() {
                getCurse(API)
            }, 5000);

            var start = false;
            var finish = false;

            function chengeStart(start, finish) {
                if (start != false && finish != false) {
                    document.location.href = "http://<?php echo $_SERVER['SERVER_NAME'] ?>:8000?start=s_" + start + '&finish=f_' + finish;
                }
            }

            $('.sidebar  table > tbody tr td:first-child').on('click', function() {
                $('.sidebar  table > tbody tr td:first-child').removeClass('ActivE');
                $(this).addClass('ActivE');

                function find(array, value) {
                    for (var i = 0; i < array.length; i++) {
                        if (array[i] == value) return i;
                    }
                    return -1;
                }

                var a = $(this).find('a'); // узел 
                var str = $(a).attr('id'); // значение id
                var id = str.replace(/[^+\d]/g, ''); // только цифры
                var i = find(ds_list, id); // ищем индекс в массиве

                var result = cu_list[i];
                start = result;

                console.log('start ' + result);
                chengeStart(start, finish);
            });

            $('.sidebar  table > tbody tr td:last-child').on('click', function() {
                $('.sidebar table > tbody tr td:last-child').removeClass('ActivE');
                $(this).addClass('ActivE');

                function find(array, value) {
                    for (var i = 0; i < array.length; i++) {
                        if (array[i] == value) return i;
                    }
                    return -1;
                }

                var a = $(this).find('a'); // узел 
                var str = $(a).attr('id'); // значение id
                var id = str.replace(/[^+\d]/g, ''); // только цифры
                var i = find(ds_list, id); // ищем индекс в массиве

                var result = cu_list[i];
                finish = result;
                console.log('finish ' + result);
                chengeStart(start, finish);
            });

            console.log(start + ' + ' + finish);

        });
